$(function(){var e=$("#carousel"),t=$("#pager"),n=$('<div class="thumb" />');e.children().each(function(){var e=$(this).attr("src");n.append('<img src="'+e.split("/large/").join("/small/")+'" />')});for(var r=0;r<e.children().length-1;r++)t.append(n.clone());e.carouFredSel({items:{visible:1,width:550,height:310},scroll:{fx:"directscroll",onBefore:function(e){var t=e.items.old.attr("src").split("/large/").join("/small/"),n=e.items.visible.attr("src").split("/large/").join("/small/"),r=i.find('img:first-child[src="'+n+'"]').parent();r.trigger("slideTo",[$('img[src="'+t+'"]',r),"next"])}}});var i=$(".thumb");i.each(function(t){$(this).carouFredSel({auto:!1,scroll:{fx:"directscroll"},items:{start:t+1,visible:1,width:100,height:80}}),$(this).click(function(){var t=$(this).children().first().attr("src").split("/small/").join("/large/");e.trigger("slideTo",[$('img[src="'+t+'"]',e),"next"])})})});